import { Component } from "@angular/core";
import * as $ from "jquery";
import { HttpClient } from "@angular/common/http";

@Component({
  selector: "app-funcao3",
  templateUrl: "./funcao3.component.html",
  styleUrls: ["./funcao3.component.css"],
})
export class Funcao3Component {
  constructor(private http: HttpClient) {}

  AbrirPagina() {
    console.log("teste 1 válidado");
    let resposta = document.getElementById("resposta");
    let p = document.createElement("p");
    $.get("http://localhost:3000/funcao3", {}, (res) => {
      console.log("teste 2 válidado");
      console.log(res);
      p.textContent = res;
      resposta?.appendChild(p);
    });
  }  

  Delete(){
    console.log("teste 1 válidado");
    let resposta2 = document.getElementById("resposta2");
    let resposta3 = document.getElementById("resposta3")
    let p = document.createElement("p");
    let p2 = document.createElement("p");
    $.post("http://localhost:3000/funcao3", {}, (res) => {
      console.log("teste 2 válidado");
      console.log(res);
      p.textContent = res;
      if(p.textContent === res){
      p2.textContent = "E A NOTADO PEDRO É 10!!";
      resposta3?.appendChild(p2);
      }
      resposta2?.appendChild(p);
    });
  }  
}

<div class="container">
  <p>Lembra o resultado da sua ultima soma?</p>
  <button class="botao" (click)="AbrirPagina()">Clica aqui pra ver</button>
  <div class="resposta" id="resposta"></div>
  <h2>------------------------------------------</h2>
  <h2>Bora deletar ela lá do Node!</h2>
  <button class="botao" (click)="Delete()">Delete!</button>
  <div class="resposta2" id="resposta2"></div>
  <div class="resposta3" id="resposta3"></div>
  <h2>------------------------------------------</h2>
  <a class="botao" href="/">Quer começar do zero?</a>
</div>
